class Harriswebworks_Newproducts_Model_Catalog_Layer extends Mage_Catalog_Model_Layer
{

    public function prepareProductCollection($collection)
    {
        parent::prepareProductCollection($collection);

        $todayStartOfDayDate  = Mage::app()->getLocale()->date()
            ->setTime('00:00:00')
            ->toString(Varien_Date::DATETIME_INTERNAL_FORMAT);

        $todayEndOfDayDate  = Mage::app()->getLocale()->date()
            ->setTime('23:59:59')
            ->toString(Varien_Date::DATETIME_INTERNAL_FORMAT);

        /** @var $collection Mage_Catalog_Model_Resource_Product_Collection */
       $collection
            ->getSelect()
            /*->setVisibility(Mage::getSingleton('catalog/product_visibility')->getVisibleInCatalogIds())*/;


        //$collection = $this->_addProductAttributesAndPrices($collection)->addStoreFilter();
            $collection = $collection->addAttributeToFilter('news_from_date', array('or'=> array(
                0 => array('date' => true, 'to' => $todayEndOfDayDate),
                1 => array('is' => new Zend_Db_Expr('null')))
            ), 'left')
            ->addAttributeToFilter('news_to_date', array('or'=> array(
                0 => array('date' => true, 'from' => $todayStartOfDayDate),
                1 => array('is' => new Zend_Db_Expr('null')))
            ), 'left')
            ->addAttributeToFilter(
                array(
                    array('attribute' => 'news_from_date', 'is'=>new Zend_Db_Expr('not null')),
                    array('attribute' => 'news_to_date', 'is'=>new Zend_Db_Expr('not null'))
                    )
              )
            ->addAttributeToSort('news_from_date', 'desc');
		//Mage::getSingleton('catalog/product_status')->addVisibleFilterToCollection($collection);
       // Mage::getSingleton('catalog/product_visibility')->addVisibleInSearchFilterToCollection($collection);
	  // Mage::log((string)$collection->getSelect(),null,'system.log',true);
	   //Mage::log($collection->getSelect(),true);
        return $this;
    }

}
